<?php

if ( ! defined( 'ABSPATH' ) )
    exit;

use Automattic\WooCommerce\Internal\DataStores\Orders\CustomOrdersTableController;

class In_Class_PandaSMS_Raporlar{

    function __construct()
    {

        if(!is_admin())
            return;

        add_action( 'add_meta_boxes', array($this, 'pandasms_siparis_sms_raporlar') );

        add_action( 'wp_ajax_pandasms_rapor_sorgula_callback', array($this, 'pandasms_rapor_sorgula_callback') );

    }

    /**
     * Sipariş sayfasında SMS gönderim raporlarının listelenmesi
     */

    function pandasms_siparis_sms_raporlar(){

        $screen = wc_get_container()->get( CustomOrdersTableController::class )->custom_orders_table_usage_is_enabled()
		? wc_get_page_screen_id( 'shop-order' )
		: 'shop_order';

        add_meta_box(
            'pandasms_siparis_sms_raporlar',
            'PandaSMS',
            array( $this, 'pandasms_siparis_sms_raporlar_callback'),
            $screen,
            'side',
            'default'
        );

    }



    function pandasms_siparis_sms_raporlar_callback( $post_or_order_object ){

        $order = ( $post_or_order_object instanceof WP_Post ) ? wc_get_order( $post_or_order_object->ID ) : $post_or_order_object;

        $pandasms_bildirim_kayitlar = In_Class_PandaSMS_Gonderim_Talebi::getSipariseAitTalepler( $order->get_id() );

        add_thickbox(); ?>
        <div id="pandasms-gonderim-sorgulama" style="display:none;">

            <div style="padding:15px 10px" class="pandasms-gonderim-sorgulama-sonuc">
                <p>
                    SMS durum bilgisi sorgulanıyor...
                </p>
            </div>

        </div>

        <div style="padding:10px 5px">

            <div>
                <img style="height:35px" src="https://pandasms.com/wp-content/uploads/2021/05/cropped-pandasms-logo.jpg" />
            </div>


            <?php

            $gonderim_bulundu = false;



            if(isset($pandasms_bildirim_kayitlar)) {

	            foreach ($pandasms_bildirim_kayitlar as $talep_detaylari) {

	                if(!$talep_detaylari->pandasms_gonderim_id>0)
	                    continue;

	                $gonderim_bulundu = true;

	                if($talep_detaylari->tetikleyici_anahtari){

		                $tetikleyici_anahtari = $talep_detaylari->tetikleyici_anahtari;

		                if(array_key_exists($tetikleyici_anahtari, pandasms_wc_get_siparis_bildirim_tetikleyici_actions()) && array_key_exists('tanim', pandasms_wc_get_siparis_bildirim_tetikleyici_actions()[$tetikleyici_anahtari]))
		                    $aciklama = pandasms_wc_get_siparis_bildirim_tetikleyici_actions()[$tetikleyici_anahtari]['tanim'];
		                else
		                    $aciklama = $tetikleyici_anahtari;

                    }else{

		                $aciklama = $talep_detaylari->islem_tipi;

                    }


		            echo '<h4 style="margin-bottom: 5px;">'.$aciklama.'</h4>';
		            echo '<a gonderim_no="' . $talep_detaylari->pandasms_gonderim_id . '" href="#TB_inline?&width=600&height=550&inlineId=pandasms-gonderim-sorgulama" class="sms_durum_sorgula thickbox button">Durumunu Sorgula</a>';

	            }

            }


            if(!$gonderim_bulundu)
                echo '<p>Gönderim bulunamadı.</p>';



            ?>

        </div>

        <div style="font-size:11px;text-align:left;margin-top:15px;float:left;color:#a9a4a4"><?php echo PANDASMS_VERSIYON; ?></div>

        <div style="clear:right;text-align:right">
            <a href="https://intense.com.tr" target="_blank">
                <img style="width:30px;"
                     src="data:image/png;base64,"/>
            </a>
        </div>

        <?php


    }


    function pandasms_rapor_sorgula_callback(){

        global $wpdb;

        $gonderimNo = sanitize_text_field( $_POST["gonderim_no"] );

        if( ! wp_verify_nonce( $_POST['pandasms_rapor_sorgula_nonce'], 'pandasms-rapor-sorgula-nonce' ) )
            die();


        $sms_sonuc = In_Class_PandaSMS_Connect::sorgula($gonderimNo);

        ?>

        <div style="margin-bottom:30px">
            <img style="height:35px" src="https://pandasms.com/wp-content/uploads/2021/05/cropped-pandasms-logo.jpg" />
        </div>

        <h3>Gönderim Sonuçları</h3>

        <?php

	    if(!$sms_sonuc){

		    echo 'Altyapısal bir problem nedeniyle, gönderim detayları sorgulanamadı.';
		    wp_die();

	    }

        ?>

        <table style="width:100%;border-collapse: collapse;" border="1px">
            <tr>
                <th width="20%">Gönderim No</th>
                <td width="80%"><?php echo $sms_sonuc->gonderim_no; ?></td>
            </tr>
            <tr>
                <th>Originator</th>
                <td><?php echo $sms_sonuc->originator; ?></td>
            </tr>
            <tr>
                <th>Harcanan Kredi</th>
                <td><?php echo $sms_sonuc->harcanan_kredi; ?></td>
            </tr>
        </table>

        <h3>Gönderim Yapılan Numaralar</h3>
        <?php foreach($sms_sonuc->sonuclar as $sonuc){ ?>
            <table border="1px" style="width:100%;border-collapse: collapse;margin-top:10px;padding:10px 0px;">

                <tr>
                    <th>Numara</th>
                    <td width="80%"><?php echo $sonuc->numara; ?></td>
                </tr>

                <tr>
                    <th>Mesaj</th>
                    <td><?php echo $sonuc->mesaj; ?></td>
                </tr>

                <tr>
                    <th>Durum</th>
                    <td><?php echo $sonuc->aciklama; ?></td>
                </tr>

                <tr>
                    <th>Ulaşma Tarihi</th>
                    <td><?php if (!empty($sonuc->ulasma_tarihi)) echo date("d/m/Y H:i:s", strtotime($sonuc->ulasma_tarihi)); ?></td>
                </tr>

                <tr>
                    <th>İşlem Tarihi</th>
                    <td><?php if (!empty($sonuc->gonderim_islem_tarihi)) echo date("d/m/Y H:i:s", strtotime($sonuc->gonderim_islem_tarihi)); ?></td>
                </tr>

            </table>
        <?php } ?>

        <?php

        wp_die();

    }



}


new In_Class_PandaSMS_Raporlar();